mapper.insert:
INSERT INTO sign (
	user_id,
	sign_state, 
	lecture_no 
	)
VALUES (
	#{userId},
	"0",
	#{lectureNo}
	)

mapper.select:
SELECT count(sign_no)
FROM sign
WHERE 
	sign.user_id = #{userId} 
	AND sign.lecture_no = #{lectureNo}

SELECT 
	s.semester_startdate
from semester s
WHERE s.semester_year = #{year}
AND s.semester_session = #{session}
