mapper.insert:
insert 
	into test (
		test_name
		, test_starttime
		, test_endtime
		, test_createdate
		, test_updatedate
		, lecture_no
	) values (
		#{testName}
		, #{testStarttime}
		, #{testEndtime}
		, now()
		, now()
		, #{lectureNo}

	)

insert 
	into question (
		question_content
		, question_answer
		, test_no
	) values (
		#{questionContent}
		, #{questionAnswer}
		, #{testNo}		
	)

insert 
	into multi_choice (
		choice_no 
		, question_no
		, choice_content
	) values (
		#{choiceNo}
		, #{questionNo}
		, #{choiceContent}
	)

